import { Component, OnInit } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { CharacterCreateService } from "../character-create.service";
import { CreateCharacter } from "../create-character.model";
import { ToastrService } from "ngx-toastr";
import { ActivatedRoute, Router } from "@angular/router";

@Component({
  selector: "app-character-create-form",
  templateUrl: "./character-create-form.component.html",
})
export class CharacterCreateFormComponent implements OnInit {
  characterForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private readonly service: CharacterCreateService,
    private toastr: ToastrService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.characterForm = this.fb.group({
      name: ["", Validators.required],
      status: ["", Validators.required],
      species: ["", Validators.required],
      type: ["", Validators.required],
      gender: ["", Validators.required],
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.characterForm.valid) {
      const updatedCharacter: CreateCharacter = this.characterForm.value;
      this.service.createCharacter(updatedCharacter).subscribe(
        () => {
          this.toastr.success("Success", "character created");
          this.router.navigate(["character"], { relativeTo: this.route });
        },
        (error) => {
          console.error("Error creating character:", error);
        }
      );
    }
  }
}
<main class="flex flex-wrap justify-center mb-3">
  <section class="card w-full max-w-xs shadow-xl m-4">
    <form
      class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4"
      [formGroup]="characterForm"
      (ngSubmit)="onSubmit()"
    >
      <div class="mb-4">
        <label
          class="block text-gray-700 text-sm font-bold mb-2"
          for="username"
        >
          Username
        </label>
        <input
          class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="name"
          formControlName="name"
          type="text"
        />
      </div>
      <div class="mb-4">
        <label class="block text-gray-700 text-sm font-bold mb-2" for="status">
          Status
        </label>
        <input
          class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="status"
          formControlName="status"
          type="text"
        />
      </div>
      <div class="mb-4">
        <label class="block text-gray-700 text-sm font-bold mb-2" for="species">
          Species
        </label>
        <input
          class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="species"
          formControlName="species"
          type="text"
        />
      </div>
      <div class="mb-4">
        <label class="block text-gray-700 text-sm font-bold mb-2" for="type">
          type
        </label>
        <input
          class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="type"
          formControlName="type"
          type="text"
        />
      </div>
      <div class="mb-4">
        <label class="block text-gray-700 text-sm font-bold mb-2" for="gender">
          gender
        </label>
        <input
          class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          id="gender"
          formControlName="gender"
          type="text"
        />
      </div>
      <div class="flex items-center justify-between">
        <button
          class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          type="submit"
          [disabled]="characterForm.invalid"
        >
          Create
        </button>
      </div>
    </form>
  </section>
</main>
