import { Component, Input } from '@angular/core'
import { Character } from '../character.model'

@Component({
  selector: 'app-character-list',
  templateUrl: './character-list.component.html'
})
export class CharacterListComponent {
  @Input() characters?: Character[]

  getBadgeStatusColor(character: Character): string {
    switch (character.status) {
      case 'Alive':
        return 'badge-success'
      case 'Dead':
        return 'badge-error'
      default:
        return 'badge-warning'
    }
  }

  getLocation(url: string): string {
    return '/location/' + url.slice(-1)
  }
}
<main class="flex flex-wrap justify-center mb-3">
  <article *ngFor="let character of characters; let i = index">
    <section class="card w-64 hover:bg-primary shadow-xl m-4">
      <figure>
        <img
          class="w-64"
          [src]="character.image"
          [alt]="character.name"
        />
      </figure>
      <section class="card-body h-96">
        <span class="card-title mb-2">{{ character.name }}</span>
        <span class="font-semibold">Species:</span> {{ character.species }}
        <div class="mt-2">
          <span class="font-semibold">Status:</span>
          <section
            class="status ml-1 indicator-item badge badge-sm {{
              getBadgeStatusColor(character)
            }}"
          >
            {{ character.status }}
          </section>
        </div>

        <span class="mt-3 font-semibold">First seen in:</span>
        <a [href]="getLocation(character.origin.url)">{{
          character.origin.name
        }}</a>
        <span class="mt-3 font-semibold">Last known location:</span>
        <a [href]="getLocation(character.location.url)">{{
          character.location.name
        }}</a>
      </section>
    </section>
  </article>
</main>
