import { Component, OnInit } from '@angular/core'
import { ActivatedRoute } from '@angular/router'
import { LocationService } from '../location.service'
import { Location } from '../location.model'

@Component({
  selector: 'app-location-list',
  templateUrl: './location-list.component.html'
})
export class LocationListComponent implements OnInit {
  locations?: Location[]
  expanded = false
  id!: string

  constructor(
    private readonly service: LocationService,
    private readonly route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.id = String(params['id'])
      this.service.getLocations(this.id).subscribe(data => {
        this.locations = data.results
      })
    })
  }
}
<h1 class="flex text-4xl justify-center mt-12 mb-6">Locations</h1>

<main class="flex flex-wrap justify-center">
  <article *ngFor="let location of locations">
    <a
      [routerLink]="['/location', location.id]"
      class="card w-64 bg-base-200 hover:bg-primary shadow-xl m-4"
    >
      <section class="card-body">
        <span class="card-title">{{ location.name }}</span>
        <span class="mt-3 mb-1">
          <span class="font-semibold">Type:</span> {{ location.type }}
        </span>
        <span>
          <span class="font-semibold">Dimension:</span> {{ location.dimension }}
        </span>
      </section>
    </a>
  </article>
</main>

<app-paginator
  pageName="location/page/"
  [pageNumber]="id"
></app-paginator>
