import { Component, OnInit } from '@angular/core'
import { Character } from '../character/character.model'
import { ActivatedRoute } from '@angular/router'
import { CharacterService } from '../character/character.service'
import { Episode } from './episode.model'
import { EpisodeService } from './episode.service'

@Component({
  selector: 'app-episode',
  templateUrl: './episode.component.html'
})
export class EpisodeComponent implements OnInit {
  characters: Character[] = []
  id!: string
  episode!: Episode

  constructor(
    private readonly episodeService: EpisodeService,
    private readonly characterService: CharacterService,
    private readonly route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.id = String(params['id'])
      this.episodeService.getEpisodeById(this.id).subscribe(data => {
        this.episode = data
        for (const characterURL of this.episode.characters) {
          this.characterService
            .getCharacterByURL(characterURL)
            .subscribe(data2 => {
              this.characters.push(data2)
            })
        }
      })
    })
  }
}
<h1 class="flex text-4xl justify-center mt-12 mb-6">{{ episode.name }}</h1>

<h2 class="flex text-2xl justify-center mt-8 mb-6">
  #{{ episode.episode }} - {{ episode.air_date }}
</h2>

<h2 class="flex text-2xl justify-center mt-12 my-6">Appearing Characters</h2>

<app-character-list [characters]="characters"></app-character-list>
