import { Component } from "@angular/core";
import { FormArray, FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Character } from "src/app/character/character.model";

@Component({
  selector: "app-character-create-form",
  templateUrl: "./character-create-form.component.html",
})
export class CharacterCreateFormComponent {
  characterForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.characterForm = this.fb.group({
      status: ["", Validators.required],
      species: ["", Validators.required],
      type: ["", Validators.required],
      gender: ["", Validators.required],
      origin: this.fb.group({
        name: [""],
        url: [""],
      }),
      location: this.fb.group({
        name: [""],
        url: [""],
      }),
      episode: this.fb.array([]),
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.characterForm.valid) {
      const updatedCharacter: Character = this.characterForm.value;
      // Handle form submission, e.g., send updatedCharacter to a service
    }
  }

  addEpisode(episode: string): void {
    this.episodeArray.push(this.fb.control(episode));
  }

  removeEpisode(index: number): void {
    this.episodeArray.removeAt(index);
  }

  get episodeArray() {
    return this.characterForm.get("episode") as FormArray;
  }
}
<main class="flex flex-wrap justify-center mb-3">
  <section class="card w-64 hover:bg-primary shadow-xl m-4">
    <form [formGroup]="characterForm" (ngSubmit)="onSubmit()">
      <div>
        <label for="status">Name</label>
        <input id="status" formControlName="namec" />
      </div>

      <div>
        <label for="status">Status</label>
        <input id="status" formControlName="status" />
      </div>

      <div>
        <label for="species">Species</label>
        <input id="species" formControlName="species" />
      </div>

      <div>
        <label for="type">Type</label>
        <input id="type" formControlName="type" />
      </div>

      <div>
        <label for="gender">Gender</label>
        <input id="gender" formControlName="gender" />
      </div>

      <div formGroupName="origin">
        <label for="origin-name">Origin Name</label>
        <input id="origin-name" formControlName="name" />

        <label for="origin-url">Origin URL</label>
        <input id="origin-url" formControlName="url" />
      </div>

      <div formGroupName="location">
        <label for="location-name">Location Name</label>
        <input id="location-name" formControlName="name" />

        <label for="location-url">Location URL</label>
        <input id="location-url" formControlName="url" />
      </div>

      <div formArrayName="episode">
        <label>Episodes</label>
        <div *ngFor="let episode of episodeArray.controls; let i = index">
          <input [formControlName]="i" />
          <button type="button" (click)="removeEpisode(i)">Remove</button>
        </div>
        <button type="button" (click)="addEpisode('')">Add Episode</button>
      </div>

      <button type="submit" [disabled]="characterForm.invalid">Submit</button>
    </form>
  </section>
</main>
