import { Component, Input } from "@angular/core";
import { Character } from "../character-create.model";

@Component({
  selector: "app-character-create-form",
  templateUrl: "./character-create-form.component.html",
})
export class CharacterFormComponent {
  @Input() characters?: Character[];

  getBadgeStatusColor(character: Character): string {
    switch (character.status) {
      case "Alive":
        return "badge-success";
      case "Dead":
        return "badge-error";
      default:
        return "badge-warning";
    }
  }

  getLocation(url: string): string {
    return "/location/" + url.slice(-1);
  }
}
<main class="flex flex-wrap justify-center mb-3">
  <article *ngFor="let character of characters; let i = index">
    <section class="card w-64 hover:bg-primary shadow-xl m-4"></section>
  </article>
</main>
